public function behaviors()
{
    return [
        [
            'class' => 'yii\filters\HttpCache', //这是http缓存
            'only' => ['index'],    //选
            //用LM最后修改时间来判断是否发生更改
            'lastModified' => function ($action, $params) {
                return filemtime('hw.txt');
